yc compute instances list | grep 'ru-' | awk -F '|' '{print $3 $6 $7}' | while read line ; do name=$(echo $line | awk '{print $1}') ; out_ip=$(echo $line | awk '{print $2}'); int_ip=$(echo $line | awk '{print $3}'); echo $name $out_ip $int_ip ;  yq -i '.all.hosts.'$name'.ansible_host="'$out_ip'" | .all.hosts.'$name'.ip="'$int_ip'" | .all.hosts.'$name'.access_ip="'$int_ip'" | .all.hosts.'$name'.ansible_python_interpreter="/usr/libexec/platform-python"' inventory/my-cluster/hosts.yaml -i; done
